PAGE;



print "[INFO] Starting PocketMine-MP for Heroku by Falk\n";
if(($f = @fsockopen("udp://localhost", 19132)) === false) {
    var_dump($f);
    print "[INFO] You don't have PocketMine running, so it will be started.\n";
    $handle = proc_open(getcwd() . "/start.sh --no-wizard --server-port=19132", [
        0 => ["pipe", "r"],
        1 => ["pipe", "w"],
        2 => ["pipe", "a"]
    ], $pipes);
    stream_set_blocking($pipes[1],0);
}
else{
    print "[INFO] A service is running on 19132, assuming it's PocketMine.\n";
    fclose($f);
}

if (($sock = socket_create(AF_INET, SOCK_STREAM, SOL_TCP)) === false) {
    print "socket_create() failed: reason: " . socket_strerror(socket_last_error()) . "\n";
}

if (socket_bind($sock, "0.0.0.0", getenv("PORT")) === false) {
    print "socket_bind() failed: reason: " . socket_strerror(socket_last_error($sock)) . "\n";
}

if (socket_listen($sock, 5) === false) {
    print "socket_listen() failed: reason: " . socket_strerror(socket_last_error($sock)) . "\n";
}

do {
    if (($msgsock = socket_accept($sock)) === false) {
        print "socket_accept() failed: reason: " . socket_strerror(socket_last_error($sock)) . "\n";
        break;
    }
    /* Send instructions. */
    $msg = str_replace("{{greeting}}", $greetings[array_rand($greetings)], "HTTP/1.1 200 OK\r\nContent-Type: text/html\r\n\r\n $defaultPage");
    socket_write($msgsock, $msg, strlen($msg));

    do {
        $buf = '';
        $headers = [];
        while ($message = socket_read($msgsock, 2048, PHP_NORMAL_READ)) {
            $buf .= $message;
            if ($message == "\r") break;
            if ($message == "\n") continue;
            list($key, $val) = explode(' ', $message, 2);
            $headers[$key] = $val;
        }
        //$html .= socket_read($soc, 1+$length);
        if($buf == ""){
            break;
        }
        if (!$buf = trim($buf)) {
            continue;
        }
        //$talkback = "PHP: You said '$buf'.\n";
        //socket_write($msgsock, $talkback, strlen($talkback));
        break; //TODO
    } while (true);
    socket_close($msgsock);
} while (true);

socket_close($sock);
if(isset($pipes) && isset($handle)) {
    fwrite($pipes[0], "stop\n");
    proc_close($handle);
}